document.addEventListener("DOMContentLoaded", function () {
            const modal = document.getElementById("ytModal");
            const iframe = document.getElementById("ytModalIframe");
            const modalTitle = document.getElementById("ytModalLabel");

            modal.addEventListener("show.bs.modal", function (event) {
                const trigger = event.relatedTarget;
                const videoId = trigger.getAttribute("data-video-id");
                const videoTitle = trigger.getAttribute("data-video-title");

                iframe.src = "https://www.youtube.com/embed/" + videoId + "?autoplay=1";
                modalTitle.textContent = videoTitle;
            });

            modal.addEventListener("hidden.bs.modal", function () {
                iframe.src = ""; // Detener el video al cerrar
                modalTitle.textContent = "Reproduciendo video";
            });
        });

